import { useCallback, useState } from "react"
import { PaginatedRequestParams, PaginatedResponse, Transaction } from "../utils/types"
import { PaginatedTransactionsResult } from "./types"
import { useCustomFetch } from "./useCustomFetch"

export function usePaginatedTransactions(): { data: PaginatedResponse<Transaction[]> | null; setTransactionApproval: (transactionId: string, approved: boolean) => void; fetchAll: () => Promise<void>; invalidateData: () => void; loading: boolean } {
  const { fetchWithCache, loading } = useCustomFetch()
  const [paginatedTransactions, setPaginatedTransactions] = useState<PaginatedResponse<
    Transaction[]
  > | null>(null)
  const [transactionApprovalStates, setTransactionApprovalStates] = useState<Record<string, boolean>>({})


  const fetchAll = useCallback(async () => {
    const response = await fetchWithCache<PaginatedResponse<Transaction[]>, PaginatedRequestParams>(
      "paginatedTransactions",
      {
        page: paginatedTransactions === null ? 0 : paginatedTransactions.nextPage,
      }
    )
    // Make sure response is not null before processing it
    if(response !== null) {
      // Add 'approved' state to each transaction.
      const updatedTransactions = response.data.map((transaction) => {
        const approvalState = transactionApprovalStates[transaction.id]
        return {
          ...transaction,
          approved: approvalState === undefined ? transaction.approved : approvalState,
        }
      })

      setPaginatedTransactions((previousResponse) => {
        if (previousResponse === null) {
          return response
        }

        return { data: [...previousResponse.data, ...response.data], nextPage: response.nextPage }
      })
    }
  }, [fetchWithCache, paginatedTransactions])

  const setTransactionApproval = useCallback((transactionId: string, approved: boolean) => {
    setPaginatedTransactions((prevTransactions) => {
      if (prevTransactions === null) {
        return prevTransactions
      }

      return {
        ...prevTransactions,
        data: prevTransactions.data.map((transaction) =>
          transaction.id === transactionId ? { ...transaction, approved } : transaction
        ),
      }
    })
  }, [])

  const invalidateData = useCallback(() => {
    setPaginatedTransactions(null)
  }, [])

  return { data: paginatedTransactions, loading, fetchAll, invalidateData, setTransactionApproval }
}
