import { useState } from "react";
import { Transaction } from "src/utils/types";

export function useTransactionApprovalState() {
  const [approvalState, setApprovalState] = useState<Record<string, boolean>>({});

  const getApprovalState = (transaction: Transaction) => approvalState[transaction.id] ?? transaction.approved;

  const setApprovalStateForTransaction = (transaction: Transaction, approved: boolean) => {
    setApprovalState((prev) => ({...prev, [transaction.id]: approved}));
  };

  return { getApprovalState, setApprovalStateForTransaction };
}
