import { useCallback, useState } from "react"
import { useCustomFetch } from "src/hooks/useCustomFetch"
import { SetTransactionApprovalParams } from "src/utils/types"
import { TransactionPane } from "./TransactionPane"
import {
  GetApprovalStateFunction,
  SetTransactionApprovalFunction,
  TransactionsComponent,
} from "./types"

export const Transactions: TransactionsComponent = ({ transactions, setApproved: SetApprovalStateFunction, getApprovalState: GetApprovalStateFunction }) => {
  const { fetchWithoutCache, loading } = useCustomFetch()
  const [approvedStatus, setApprovedStatus] = useState<Record<string, boolean>>({});

  const setApproved = useCallback((transactionId: string, approved: boolean) => {
    setApprovedStatus(prevStatus => ({ ...prevStatus, [transactionId]: approved }))
  }, [])

  const setTransactionApproval = useCallback<SetTransactionApprovalFunction>(
    async ({ transactionId, newValue }) => {
      await fetchWithoutCache<void, SetTransactionApprovalParams>("setTransactionApproval", {
        transactionId,
        value: newValue,
      })
      setApproved(transactionId, newValue);
    },
    [fetchWithoutCache, setApproved]
  )

  const getApprovalState = useCallback((transactionId: string) => {
    return approvedStatus[transactionId] ?? false;
  }, [approvedStatus]);

  if (transactions === null) {
    return <div className="RampLoading--container">Loading...</div>
  }

  return (
    <div data-testid="transaction-container">
      {transactions.map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          loading={loading}
          approved={getApprovalState(transaction.id)}
          setTransactionApproval={setTransactionApproval}
          setApproved={setApproved}
        />
      ))}
    </div>
  )
}
