  //       if (previousResponse === null) {
  //         return response;
  //       }
  //
  //       return { data: [...previousResponse.data, ...response.data], nextPage: response.nextPage };
  //     })
  //
  //     const newApprovalState = response.data.reduce((acc: Record<string, boolean>, transaction: Transaction) => {
  //       acc[transaction.id] = transaction.approved;
  //       return acc;
  //     }, approvalState);
  //     setApprovalState(newApprovalState);
  //
  //     if (response.nextPage) {
  //       return fetchAll();
  //     }
  //   }
  // }, [fetchWithCache, paginatedTransactions, approvalState]);


  const invalidateData = useCallback(() => {
    setPaginatedTransactions(null)
  }, [])

  return { data: paginatedTransactions, loading, fetchAll, invalidateData }
}
